    #[test]
    fn parse_simple_expression() {
        let ast = parse("1 + 2").unwrap();
        assert_eq!(ast.root.kind, NodeKind::BinaryOp);
    }

    #[test]
    fn parse_nested_parens() {
        let ast = parse("(1 + (2 * 3))").unwrap();
        assert_eq!(ast.depth(), 3);
    }
